mapper.select:
SELECT
    caidx, caId, caMkey, caPvalue, caregdate, caStorageDate, caea, caMtable
FROM B_Cart WHERE CaId = #{caId}

mapper.insert:
INSERT INTO B_Cart (
    caId, caMkey, caPvalue, caregdate, caStorageDate, caea, caMtable
)
VALUES (
    #{caId}, #{caMkey}, #{caPvalue}, #{caregdate}, #{caStorageDate}, #{caea}, #{caMtable}
)

mapper.update:
UPDATE B_Cart
SET Caea = #{caEA}
WHERE Caidx = #{caIdx}
